<?php
  get_header(); 
  happypay_mainbody_before();
?>

<main id="site-main">
  <?php happypay_mainbody_start(); ?>
    
    <?php if(have_posts()): while(have_posts()): the_post(); ?>
      <article role="article" id="post_<?php the_ID()?>" <?php post_class("mb-5")?>>
        <section class="wrap-md pb-5 entry-content">
          <?php the_content()?>
          <?php wp_link_pages(); ?>
        </section>
      </article>
    <?php
      endwhile;
      else :
        get_template_part('loops/404');
      endif;
    ?>  

  <?php //happypay_mainbody_end(); ?>
</main>

<?php 
  happypay_mainbody_after();
  get_footer(); 
?>